INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102264, 'Alimemazine', 'Methyldopa', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102383/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102265, 'Vardenafil', 'Methyldopa', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102384/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102266, 'Vasopressin', 'Methyldopa', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria,