INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102302, 'Clopidogrel', 'Phenyl salicylate', 'Moderate', 'Clopidogrel has been shown to potentiate the inhibition of platelet aggregation due to aspirin. The safety of chronic administration of aspirin or other salicylates with clopidogrel has not been established.', NULL, 'Until further data are available, caution is recommended, especially in patients at risk of bleeding (i.', NULL, 'Until further data are available, caution is recommended, especially in patients at risk of bleeding (i.e., GI ulceration), if clopidogrel is coadministered on a long-term basis with drugs that may cause GI lesions. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, red or black stools, or bloody or coffee-ground emesis. Patients should also be counseled to avoid any other over-the-counter salicylate products.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102421/', '[1] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000): 305-13[2] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102303, 'Clove', 'Phenyl salicylate', 'Minor', 'Clove and clove oil may potentiate the effects of anticoagulants and other drugs that may affect hemostasis such as platelet inhibitors, thrombolytic agents, and nonsteroidal anti-inflammatory drugs, possibly increasing the risk of bleeding. Eugenol, the active constituent of clove and clove oil, has been shown to be an inhibitor of platelet activity.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used clove and clove oil extensively prior to receiving treatment with anticoagulants or other drugs that may affect hemostasis, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102422/', '[1] Srivasta KC "Antiplatelet principles from a food spice clove (Syzygium aromaticum L) [corrected]" Prostaglandins Leukot Essent Fatty Acids 48 (1993): 363-72[2] Saeed SA, Gilani AH "Antithrombotic activity of clove oil." J Pak Med Assoc 44 (1994): 112-5[3] Heck AM, DeWitt BA, Lukes AL "Potential interactions b