INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102480, 'Solriamfetol', 'Methylene blue', 'Major', 'Coadministration of solriamfetol with other drugs that also inhibit the reuptake of dopamine or norepinephrine, including monoamine oxidase inhibitors (MAOIs), may increase the risk of hypertensive reactions including death, stroke, myocardial infarction, aortic dissection, pulmonary edema, and renal failure. Additionally, MAOIs can increase dopaminergic and noradrenergic activity by inhibiting the metabolism of dopamine and norepinephrine.', NULL, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated.', NULL, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with solriamfetol.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102599/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Zyvox (linezolid)" Pharmacia and Upjohn, Kalamazoo, MI.[4] Zetin M, Plon L, DeAntonio M "MAOI reaction with powdered protein dietary supplement." J Clin Psychiatry 48 (1987): 499[5] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[6] Pohl R, Balon R, Berchou R "Reaction to chicken nuggets in a patient taking an MAOI." Am J Psychiatry 145 (1988): 651[7] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[8] Tailor SA, Shulman KI, Walker SE, Moss J, Gardner D "Hypertensive episode associated with phenelzine and tap beer--a 