    ) VALUES (1384980, 'Ceritinib', 'Ponatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Caution is recommended if concomitant use is required, and the recommended starting dosage of ponatinib should be reduced to 30 mg once daily.', NULL, 'Caution is recommended if concomitant use is required, and the recommended starting dosage of ponatinib should be reduced to 30 mg once daily. Patients may be at increased risk for adverse effects such as thromboembolism, ischemia, congestive heart failure, hypertension, hepatotoxicity, pancreatitis, myelosuppression, hemorrhage, and fluid retention.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
