INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102570, 'Metronidazole (topical)', 'Lithium carbonate', 'Moderate', 'Limited data suggest that coadministration with metronidazole may increase the risk of lithium toxicity. The mechanism of interaction has not been described, and it is not known whether the interaction occurs with other nitroimidazoles or vaginally administered metronidazole.', NULL, 'Pharmacologic response and serum lithium levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary.', NULL, 'Pharmacologic response and serum lithium levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary. Patients should be advised to notify their physician if they experience early symptoms of lithium toxicity such as diarrhea, vomiting, drowsiness, tremor, lack of coordination, ataxia, or muscular weakness. The same precaution may be applicable during therapy with tinidazole or vaginal preparations of metronidazole, although clinical data are lacking.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102689/', '[1] Teicher MH, Altesman RI, Cole JO, et al "Possible nephrotoxic interaction of lithium and metronidazole." JAMA 257 (1987): 3365-6[2] "Product Information. Eskalith (lithium)." SmithKline Beecham, Philadelphia, PA.[3] "Product Information. Flagyl (metronidazole)." Searle, Skokie, IL.[4] "Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc, Arlington Heights, IL.[5] "Product Information. Metrogel-Vaginal (metronidazole)." Curatek Pharma