INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102577, 'Ribociclib', 'Micafungin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. Administration of erythromycin, a moderate CYP450 3A4 inhibitor, is predicted to increase ribociclib Cmax and AUC by 1.3- and 1.9-fold, respectively.', NULL, 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for ribociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, some manufacturers of ribociclib recommend that concomitant use with moderate CYP450 3A4 inhibitor verapamil should be avoided (AU, UK). Patients should be monitored for the development of ribociclib-related adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102696/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[5] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102578, 'Sirolimus', 'Micafungin', 'Moderate', 'Coadministration with micafungin may increase the serum concentrations of sirolimus. The exact mechanism of interaction is unknown but may involve inhibition of sirolimus metabolism, since micafungin has been shown to be a weak inhibitor of CYP450 3A in vitro.', NULL, 'Due to the narrow therapeutic index of sirolimus, pharmacologic response and sirolimus blood levels should be closely monitored following initiation or discontinuation of micafungin therapy in patients who are stabilized on their antirejection regimen, and the sirolimus dosage adjusted accordingly.', NULL, 'Due to the narrow therapeutic index of sirolimus, pharmacologic response and sirolimus blood levels should be closely monitored following initiation or discontinuation of micafungin therapy in patients who are stabilized on their antirejection regimen, and the sirolimus dosage adjusted accordingly. Patients should be monitored for potential signs and symptoms of sirolimus toxicity such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102697/', '[1] "Product Information. Mycamine (micafungin)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102579, 'Anisindione', 'Miconazole (topical)', 'Major', 'Systemically or even topically administered miconazole may increase the plasma concentrations and hypoprothrombinemic effect of warfarin. The proposed mechanism is miconazole inhibition of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of the more active S(-) enantiomer of warfarin.', NULL, 'Patients receiving warfarin or other oral anticoagulants should be closely monitored during concomitant therapy with miconazole.', NULL, 'Patients receiving warfarin or other oral anticoagulants should be closely monitored during concomitant therapy with miconazole. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of miconazole therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Some authorities consider the use of oral miconazole with warfarin to be contraindicated. Although vaginally administered miconazole is generally less than 2% absorbed in healthy women of childbearing age, increased absorption may occur in the presence of atrophic vaginal epithelium. Therefore, the same precaution is applicable when vaginal formulations of miconazole is prescribed to women receiving oral anticoagulant therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102698/', '[1] Colquhoun MC, Daly M, Stewart P, Beeley L "Interaction between warfarin and miconazole oral gel." Lancet 1 (1987): 695-6[2] Shenfield GM, Page M "Potentiation of warfarin action by miconazole oral gel." Aust N Z J Med 21 (1991): 928[3] Watson PG, Lochan RG, Redding VJ "Drug interaction with coumarin derivative anticoagulants." Br Med J 285 (1982): 1045-6[4] Gutierrez MA, Olalla I, Muruzabal MJ, Ortiz M, Peralta FG "Miconazole oral gel enhances acenocoumarol anticoagulant activity. Report of three cases." Eur J Clin Pharmacol 52(Suppl) (1997): A132[5] Evans J, Orme DS, Sedgwick ML, Youngs GR "Treating oral candidiasis: potentially fatal." Br Dent J 182 (1997): 452[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Ortin M, Olalla JI, Muruzabal MJ, Peralta FG, Gutierrez MA "Miconazole oral gel enhances acenocoumarol anticoagulant activity: A report of three cases." Ann Pharmacother 33 (1999): 175-7[8] Visser LE, Penning-Van Beest FJ, Kasbergen AA, et al. "Overanticoagulation associated with combined use of antifungal agents and coumarin anticoagulants." Clin Pharmacol Ther 71 (2002): 496-502[9] Marco M "Retroperitoneal haematoma and small bowel intramural haematoma caused by warfarin and miconazole interaction." Int J Oral Maxillofac Surg 27 (1998): 485[10] Ariyaratnam S, Thakker NS, Sloan P, Thornhill MH "Potentiation of warfarin anticoagulant activity by miconazole oral gel." BMJ 314 (1997): 349[11] Niwa T, Shiraga T, Takagi A "Effect of antifungal drugs on cytochrome P450 (CYP) 2C9, CYP2C19, CYP3A4 activities in human liver microsomes." Biol Pharm Bull 28 (2005): 1805-8[12] Pemberton MN, Sloan P, Ariyaratnam S, Thakker NS, Thornhill MH "Derangement of warfarin anticoagulation by miconazole oral gel." Br Dent J 184 (1998): 68-9[13] Thirion DJ, Farquhar Zannetti LA "Potentiation of warfarin''s hypoprothrombinemic effect with miconazole vaginal suppositories." Pharmacotherap