INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102720, 'Erdafitinib', 'Sodium phosphate, monobasic', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102839/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102721, 'Foscarnet', 'Sodium phosphate, monobasic', 'Moderate', 'Coadministration with other drugs that can reduce serum calcium may potentiate the risk of hypocalcemia associated with foscarnet. Foscarnet chelates with divalent metal ions and causes a dose-related reduction in ionized serum calcium.', NULL, 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations.', NULL, 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations. Serum creatinine and electrolyte levels should be assessed prior to initiating therapy and frequently during therapy. Patients should be advised to promptly notify their physician if they experience potential symptoms of hypocalcemia such as perioral tingling, numbness in extremities, and paresthesias.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102840/', '[1] "Product Information. Foscavir (foscarnet)." Astra USA, Westborough, MA.[2] "Foscarnet: a review." Drugs 41 (1991): 106-29[3] Youle M, Clarbour J, Gazzard B, Chanas A "Severe hypocalcemia in AIDS patients treated with foscarnet and pentamidine." Lancet 1 (1988): 1455-6[4] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102722, 'Sodium phosphate, monobasic', 'Lactulose', 'Moderate', 'In the treatment of portal-systemic encephalopathy, the loose stools produced by concomitant use of other laxatives may falsely suggest that adequate lactulose dosage has been achieved.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102841/', '[1] "Product Information. Cephulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Generlac (lactulose)." Morton Grove Pharmaceuticals Inc, Morton Grove, IL.[3] "Product Information. Enulose (lactulose)." United Research Laboratories/Mutual Pharmaceutical Company, Philadelphia, PA.[4] "Product Information. Chronulac (lactulose)." Hoechst Marion-Roussel Inc, Kansas City, MO.[5] "Product Information. Metamucil (psyllium)." Procter and Gamble Pharmaceutic, Cincinnati, OH.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102723, 'Opium', 'Naltrexone', 'Major', 'Naltrexone can antagonize the effects of opioids via competitive inhibition of opioid receptors. Patients receiving naltrexone may not benefit from opioid-containing medications such as cough and cold products, antidiarrheal preparations, and narcotic analgesics. Likewise, patients dependent on opioids may experience withdrawal symptoms when given naltrexone. Following use of naltrexone, patients may have increased sensitivity to opioids.', NULL, 'The use of naltrexone is considered contraindicated in patients receiving opioids or dependent on opioids, including those maintained on opiate agonists (e.', NULL, 'The use of naltrexone is considered contraindicated in patients receiving opioids or dependent on opioids, including those maintained on opiate agonists (e.g., methadone) or partial agonists (e.g., buprenorphine). Naltrexone should also not be given to patients in acute opioid withdrawal. In an urgent situation when analgesia may be required in a patient who has received full blocking doses of naltrexone, consideration should be given to regional analgesia, conscious sedation with a benzodiazepine, use of non-opioid analgesics, or general anesthesia. If opioid analgesia is required, the amount of opioid needed may be greater than usual, and the resulting respiratory depression may be deeper and more prolonged. A rapidly-acting opioid analgesic that minimizes the duration of respiratory depression is preferred. Clinicians should be aware that reversal of full naltrexone blockade by administration of large doses of opiates can cause histamine release. Therefore, patients may experience non-opioid receptor-mediated effects such as facial swelling, itching, generalized erythema, and bronchoconstriction. Irrespective of the drug chosen to reverse naltrexone blockade, the patient should be monitored closely by appropriately trained personnel in a setting equipped and staffed for cardiopulmonary resuscitation.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102842/', '[1] "Product Information. ReVia (naltrexone)." DuPont Pharmaceuticals, Wilmington, DE.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365206);
