INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102753, 'Iron', 'Zinc sulfate', 'Minor', 'Iron and zinc may reduce each other''s absorption when they are coadministered orally. The mechanism of interaction is unknown. In clinical studies, concurrent supplementation with iron and zinc was associated with reduced efficacy in improving the growth and iron and zinc status of malnourished infants compared to supplementation with either one alone. In any case, the clinical significance is probably minimal in healthy individuals. However, monitoring for reduced therapeutic response may be advisable in malnourished patients. Limited data suggest that the magnitude of the interaction may be lessened by separating the times of administration of each by 12 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102872/', '[1] Sreedhar B "Conflicting evidence of iron and zinc interactions in humans: does iron affect zinc absorption?" Am J Clin Nutr 78 (2003): 1226; author reply 1226-7[2] Lind T, Lonnerdal B, Stenlund H, et al. "A community-based randomized controlled trial of iron and zinc supplementation in Indonesian infants: interactions between iron and zinc." Am J Clin Nutr 77 (2003): 883-90', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102754, 'Cinoxacin', 'Selenium', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102873/', '[1] Teng R, Dogolo LC, Willavize SA, Friedman HL, Vincent J "Effect of Maalox and omeprazole on the bioavailability of trovafloxacin." J Antimicrob Chemother 39 Suppl B (1997): 93-7[2] Noyes M, Polk RE "Norfloxacin and absorption of magnesium-aluminum." Ann Intern Med 109 (1988): 168-9[3] Nix DE, Wilton JH, Ronald B, Distlerath L, Williams VC, Norman A "Inhibition of norfloxacin absorption by antacids." Antimicrob Agents Chemother 34 (1990): 432-5[4] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[5] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[6] "Product Informati