INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102902, 'Nadolol', 'Methacholine', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment. Beta-2-adrenergic blockade increases airway resistance. Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', NULL, 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', NULL, 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103021/', '[1] "Product Information. Provocholine (methacholine)." Methapharm Inc, Brantford, ON.', 1767365206);
