INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102999, 'Esomeprazole', 'Naproxen', 'Moderate', 'Theoretically, proton pump inhibitors may decrease the gastrointestinal absorption of enteric-coated naproxen, which requires an acidic environment for dissolution. The proposed mechanism is an increase in gastric pH (i.e. decreased gastric acidity) induced by proton pump inhibitors. In patients treated with proton pump inhibitors, the possibility of a reduced or subtherapeutic response to enteric-coated naproxen should be considered.', NULL, 'Concomitant use of these drugs is generally not recommended.', NULL, 'Concomitant use of these drugs is generally not recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103118/', '[1] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[2] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[3] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767365206);
