    ) VALUES (1385621, 'Aluminum hydroxide', 'Chenodeoxycholic acid', 'Moderate', 'INTERVAL: Aluminum-based antacids have been shown to adsorb bile acids in vitro and may be expected to interfere with the gastrointestinal absorption of chenodeoxycholic acid, which is a naturally occurring human bile acid.', NULL, 'To minimize the potential for interaction, it may be helpful to separate the dosing of chenodeoxycholic acid and aluminum-based antacids by several hours.', NULL, 'To minimize the potential for interaction, it may be helpful to separate the dosing of chenodeoxycholic acid and aluminum-based antacids by several hours. However, clinical data are lacking to support a recommendation.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
