    ) VALUES (1385624, 'Cholestyramine', 'Chenodeoxycholic acid', 'Moderate', 'INTERVAL: Bile acid sequestering agents may bind to and decrease the gastrointestinal absorption of chenodeoxycholic acid, which is a naturally occurring human bile acid.', NULL, 'As with other drugs whose absorption may be reduced by bile acid sequestering agents, chenodeoxycholic acid should be administered at least 4 hours before the bile acid sequestering agent.', NULL, 'As with other drugs whose absorption may be reduced by bile acid sequestering agents, chenodeoxycholic acid should be administered at least 4 hours before the bile acid sequestering agent. However, adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
