    ) VALUES (1385654, 'Chenodeoxycholic acid', 'Probucol', 'Moderate', 'Drugs like estrogens, oral contraceptives, fibrates, and possibly other lipid-lowering agents that increase biliary cholesterol secretion may potentiate the risk of cholesterol gallstones and counteract the effectiveness of chenodeoxycholic acid.', NULL, 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', NULL, 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
