        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1385662, 'Desvenlafaxine', 'Cetirizine', 'Moderate', 'Concurrent use of cetirizine or levocetirizine with alcohol or other agents that exhibit central nervous system (CNS) depressant effects may result in additive impairment of mental alertness and performance.', NULL, 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible.', NULL, 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
