INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103075, 'Irbesartan', 'Naproxen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103194/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103076, 'Itraconazole', 'Esomeprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of the azole antifungal agents, itraconazole and ketoconazole, both of which require an acidic environment for dissolution. By increasing gastric pH and reducing the acidity, proton pump inhibitors can decrease bioavailability of the azoles by 75% to 80%.', NULL, 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose.', NULL, 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose. Administration with an acidic beverage such as Coca-Cola(R) or Pepsi(R) may also help. However, clinicians should still consider the possibility of a reduced or subtherapeutic antifungal effect. It may be appropriate to switch to an agent like fluconazole or terbinafine whose absorption is not affected by stomach pH.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103195/', '[1] Piscitelli SC, Goss TF, Wilton JH, D''Andrea DT, Goldstein H, Schentag JJ "Effects of ranitidine and sucralfate on ketoconazole bioavailability." Antimicrob Agents Chemother 35 (1991): 1765-71[2] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.[3] Bottiger Y, Tybring G, Gotharson E, Bertilsson L "Inhibition of the sulfoxidation of omeprazole by ketoconazole in poor and extensive metabolizers of S-mephenytoin." Clin Pharmacol Ther 62 (1997): 384-91[4] "Product Information. 