INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103120, 'Esomeprazole', 'Oxcarbazepine', 'Moderate', 'Coadministration with oxcarbazepine may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by oxcarbazepine and its active metabolite 10-monohydroxy derivative, or MHD. In vivo, the plasma levels of phenytoin, a CYP450 2C19 substrate, increased by up to 40% when oxcarbazepine was given at dosages above 1200 mg/day.', NULL, 'Caution is advised if higher dosages of oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if higher dosages of oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oxcarbazepine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103239/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Trileptal (oxcarbazepine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[4] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[5] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med