INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103457, 'Avanafil', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of avanafil, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103576/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[3] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[4] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[5] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103458, 'Avapritinib', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the starting dosage of avapritinib from 300 mg orally once daily to 100 mg orally once daily.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103577/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103459, 'Axitinib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103578/', '[1] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103460, 'Bedaquiline', 'Palonosetron', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103579/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103461, 'Bepridil', 'Palonosetron', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103580/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Yamreudeewong W, DeBisschop M, Martin L, Lower D "Potentially Significant Drug Interactions of Class III Antiarrhythmic Drugs." Drug Saf 26 (2003): 421-38[3] "Product Information. Norpace (disopyramide)." Searle, 