INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103473, 'Butorphanol', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of butorphanol according to the product labeling. Increased butorphanol concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease butorphanol plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to butorphanol.', NULL, 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', NULL, 'Patients receiving butorphanol with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath. When discontinuing CYP450 3A4 inhibitors, monitor patients closely at frequent intervals and consider increasing the opioid dosage if needed to maintain adequate analgesia or if symptoms of opioid withdrawal occur.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103592/', '[1] "Product Information. Butorphanol Tartrate (butorphanol)." Apotex Corporation, Weston, FL.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103474, 'Cabazitaxel', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if cabazitaxel is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to cabazitaxel should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the cabazitaxel dosage adjusted as necessary. Regular monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience potential signs and symptoms of cabazitaxel toxicity such as infection, fever, chills, diarrhea, unusual bruising or bleeding, and burning, tingling, or numbness in the hands and feet.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103593/', '[1] "Product Information. Jevtana (cabazitaxel)." sanofi-aventis , Bridgewater, NJ.', 1767363023);
