INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103476, 'Capmatinib', 'Netupitant', 'Moderate', 'Coadministration of capmatinib with moderate CYP450 3A4 inhibitors may increase the risk and severity of capmatinib adverse effects, such as interstitial lung disease, pneumonitis, and hepatotoxicity. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', NULL, 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor.', NULL, 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103595/', '[1] "Product Information. Tabrecta (capmatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363023);
