INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103488, 'Cobicistat', 'Netupitant', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant. When a single 300 mg-0.5 mg dose of netupitant-palonosetron was administered following 400 mg once daily administration of the potent CYP450 3A4 inhibitor ketoconazole for 12 days, mean netupitant peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 25% and 140%, respectively, while mean Cmax and AUC of palonosetron increased by 15% and 10%, respectively. No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103607/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103489, 'Cobimetinib', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If short-term (14 days or less) use of a moderate CYP450 3A4 inhibitor is required during treatment with cobimetinib 60 mg once daily, the manufacturer suggests reducing the cobimetinib dose to 20 mg. Following discontinuation of the moderate CYP450 3A4 inhibitor, the previous dose of 60 mg may be resumed. An alternative to the moderate CYP450 3A4 inhibitor should be used in patients who are already taking a reduced dose of cobimetinib (40 or 20 mg daily).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103608/', '[1] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.', 1767363023);
