INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103491, 'Conivaptan', 'Netupitant', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant. When a single 300 mg-0.5 mg dose of netupitant-palonosetron was administered following 400 mg once daily administration of the potent CYP450 3A4 inhibitor ketoconazole for 12 days, mean netupitant peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 25% and 140%, respectively, while mean Cmax and AUC of palonosetron increased by 15% and 10%, respectively. No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103610/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.', 1767363023);
