INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103602, 'Isradipine', 'Netupitant', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy. The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103721/', '[1] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc, Woodbridge Township, NJ.[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767365206);
