INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103623, 'Netupitant', 'Levamlodipine', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103742/', '[1] Sasaki M, Maeda A, Fujimura A "Influence of diltiazem on the pharmacokinetics of amlodipine in elderly hypertensive patients." Eur J Clin Pharmacol 57 (2001): 85-6[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Norvasc (amlodipine)." Pfizer US Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103624, 'Netupitant', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103743/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103625, 'Palonosetron', 'Levofloxacin', 'Moderate', 'Certain quinolones, including levofloxacin, norfloxacin, and ofloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103744/', '[1] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporati