INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103797, 'Stiripentol', 'Palonosetron', 'Moderate', 'The concomitant administration of stiripentol may significantly increase plasma concentrations, pharmacologic effects, and toxicities of drugs that are substrates of CYP450 2C19, 3A4, and/or 2D6. The mechanism is inhibition of cytochrome P-450 metabolism by stiripentol.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use. Dosage adjustments may be required if increased plasma levels or effects are observed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103916/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[6] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103798, 'Sufentanil', 'Palonosetron', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103917/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Sancuso (granisetron)." ProStrakan Group, Bedminster, NJ.[6] "Product Information. Aloxi (palonosetron)." MGI Pharma Inc, Minnetonka, MN.[7] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103799, 'Sulfamethoxazole', 'Palonosetron', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103918/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Crouch MA, Limon L, Cassano AT "Clinical relevance and management of drug-related QT interval prolongation." Pharmacotherapy 23 (2003): 881-908[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wiener I, Rubin D, Martinez E, et al "QT prolongation and paroxysmal ventricular tachycardia occurring during fever following trimethoprim-sulfamethoxazole administration." Mt Sinai J Med 48 (1981): 53-5[5] Lopez JA, Harold JG, Rosenthal MC, Oseran DS, Schapira JN, Peter T "QT prolongation and torsades de pointes after administration of trimethoprin-sulfamethoxazole." Am J Cardiol 59 (1987): 376-7[6] Darpo B "Spectrum of drugs prolonging QT interval and the incidence of torsades de pointes." Eur Heart J Suppl 3(Suppl K) (2001): K70-80[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103800, 'Sunitinib', 'Palonosetron', 'Moderate', 'Sunitinib can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if sunitinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if sunitinib is used in combination with other drugs that can prolong the QT interval. Periodic monitoring with on-treatment electrocardiograms and serum electrolytes (magnesium, potassium) should be considered. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103919/', '[1] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103801, 'Suvorexant', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103920/', '[1] "Product Information. Belsomra (suvorexant)." Merck & Company Inc, Whitehouse Station, NJ.[2] "Product Information. Belsomra (suvorexant)." Merck & Company Inc, Whitehouse Station, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103802, 'Tacrine', 'Palonosetron', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103921/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103803, 'Tacrolimus', 'Netupitant', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103922/', '[1] Christians U, Schmidt G, Bader A, et al "Identification of drugs inhibiting the in vitro metabolism of tacrolimus by human liver microsomes." Br J Clin Pharmacol 41 (1996): 187-90[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product 