INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103909, 'Niacin', 'Mipomersen', 'Major', 'Coadministration of mipomersen with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Mipomersen can cause elevations in serum transaminases and hepatic steatosis.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if mipomersen is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, lomitapide, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Mipomersen has not been studied with other LDL-lowering agents that can also increase hepatic fat, thus concomitant use is not recommended. Patients treated with mipomersen should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking mipomersen not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104028/', '[1] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Kynamro (mipomersen)." Genzyme Corporation, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103910, 'Niacin', 'Naltrexone', 'Moderate', 'Coadministration of naltrexone with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Naltrexone, especially in larger than recommended doses (more than 50 mg/day), has been associated with hepatocellular injury, hepatitis, and elevations in liver transaminases and bilirubin. Other potential causative or contributory etiologies identified include preexisting alcoholic liver disease, hepatitis B and/or C infection, and concomitant usage of other hepatotoxic drugs.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Periodic monitoring of hepatic function is advisable.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104029/', '[1] "Product Information. ReVia (naltrexone)." DuPont Pharmaceuticals, Wilmington, DE.', 1767365206);
