INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103938, 'Trabectedin', 'Niacin', 'Moderate', 'The risk of rhabdomyolysis may be increased during coadministration of trabectedin with other agents that are also associated with this toxicity, such as colchicine, alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin. The risk of liver injury may be increased during coadministration of trabectedin with other potentially hepatotoxic agents, including alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin.', NULL, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis.', NULL, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis. Patients should be advised to seek medical attention if they experience unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Caution is advised when trabectedin is used with other agents that are potentially hepatotoxic. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104057/', '[1] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
