INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103940, 'Bifidobacterium longum infantis', 'Nifurtimox', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104059/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103941, 'Nifurtimox', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104060/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103942, 'Tipranavir', 'Nifurtimox', 'Moderate', 'Ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, and tipranavir capsules all contain alcohol, which may produce a disulfiram-like reaction when coadministered with drugs that can inhibit aldehyde dehydrogenase (ALDH) such as nitroimidazoles (e.g., metronidazole, tinidazole), nitrofurans (e.g., furazolidone, nifurtimox), and cephalosporins with an N-methylthiotetrazole (NMTT) side chain that structurally resembles disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', NULL, 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', NULL, 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104061/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Williams CS, Woodcock KR "Do ethanol and metronidazole interact to produce a disulfiram-like reaction?." Ann Pharmacother 34 (2000): 255-7[3] Uri JV, Parks DB "Disulfiram-like reaction to certain cephalosporins." Ther Drug Monit 5 (1983): 219-24[4] Alexander I "Alcohol-antabuse syndrome in patients receiving metronidazole during gynaecological treatment." Br J Clin Pract 39 (1985): 292-3[5] "Product Information. Flagyl (metronidazole)." Searle, Skokie, IL.[6] "Product Information. Metrogel-Vaginal (metronidazole)." Curatek Pharmaceuticals Ltd, Elk Grove Village, IL.[7] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc, Arlington Heights, IL.[9] Neu HC, Prince AS "Interaction between moxalactam and alcohol." Lancet June (1980): 1422[10] Krulewitch CJ "An unexpected adverse drug effect." J Midwifery Womens Health 48 (2003): 67-8[11] Cina SJ, Russell RA, Conradi SE "Sudden death due to metronidazole/ethanol interaction." Am J Forensic Med Pathol 17 (1996): 343-6[12] Elenbaas RM "Drug therapy reviews: management of the disulfiram-alcohol reaction." Am J Hosp Pharm 34 (1977): 827-31[13] Freundt KJ, Schreiner E, Christmann-Kleiss U "Cefamandole: a competitive inhibitor of aldehyde dehydrogenase." Infection 13 (1985): 91[14] van Ieperen L "Sudden death during disulfiram-ethanol reaction." S Afr Med J 66 (1984): 165[15] Visapaa JP, Tillonen JS, Kaihovaara PS, Salaspuro MP "Lack of disulfiram-like reaction with metronidazole and ethanol." Ann Pharmacother 36 (2002): 971-4[16] Foster TS, Raehl CL, Wilson HD "Disulfiram-like reaction associated with a parenteral cephalosporin." Am J Hosp Pharm 37 (1980): 858-9[17] Buening MK, et al "Disulfiram-like reaction to beta-lactams." JAMA 245 (1981): 2047[18] "Product Information. Furoxone (furazolidone)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[19] Kline SS, Mauro VF, Forney RB Jr, et al "Cefotetan-induced disulfiram-type reactions and hypoprothrombinemia." Antimicrob Agents Chemother 31 (1987): 1328-31[20] Freundt KJ, Kitson TM "Inactivation of aldehyde dehydrogenase by a putative metabolite of cefamandole." Infection 14 (1986): 44-7[21] McMahon FG, Ryan JR, Jain AK, LaCorte W, Ginzler F "Absence of disulfiram-type reactions to single and multiple doses of cefonicid: a placebo-controlled study." J Antimicrob Chemother 20 (1987): 913-8[22] Umeda S, Arai T "Disulfiram-like reaction to moxalactam after celiac plexus alcohol block." Anesth Analg 64 (1985): 377[23] Reeves DS, Davies AJ "Antabuse effect with cephalosporins." Lancet 2 (1980): 540[24] McMahon FG "Disulfiram-like reaction to a cephalosporin." JAMA 243 (1980): 2397[25] Portier H, Chalopin JM, Freysz M, Tanter Y "Interaction between cephalosporins and alcohol." Lancet 08/02/80 (1980): 263[26] Shimada J, Hayashi Y, Nakamura K "Cefmetazole: clinical evaluation of efficacy and safety in Japan." Drugs Exp Clin Res 11 (1985): 181-94[27] Harries DP, Teale KF, Sunderland G "Metronidazole and alcohol: potential problems." Scott Med J 35 (1990): 179-80[28] Brown KR, Guglielmo BJ, Pons VG, Jacobs RA "Theophylline elixir, moxalactam, and a disulfiram reaction." Ann Intern Med 97 (1982): 621-2[29] Giannini AJ, DeFrance DT "Metronidazole and alcohol: potential for combinative abuse." J Toxicol Clin Toxicol 20 (1983): 509-15[30] Jones RO "Death following the ingestion of alcohol in an antabuse treated patient." Can Med Assoc J 60 (1949): 609-12[31] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[32] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[33] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[34] Cerner Multum, Inc. "Australian Product Information." O 0[35] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (103943, 'Oxybuprocaine (ophthalmic)', 'Nitric Oxide', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', NULL, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104062/', '[1] Guay J "Methemoglobinemia related to local anesthetics: a summary of 242 episodes." Anesth Analg 108 (2009): 837-45[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Hurricaine (benzocaine topical)." Beutlich, Waukegan, IL.[4] "Product Information. Chloraseptic (benzocaine-menthol topical)." Prestige Brands (formerly MedTech), Tarrytown, NY.[5] Skold A, Cosco DL, Klein R "Methemoglobinemia: pathogenesis, diagnosis, and management." South Med J 104 (2011): 757-61[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Karim A, Ahmed S, Siddiqui R, Mattana J "Methemoglobinemia complicating topical lidocaine used during endoscopic procedures." Am J Med 111 (2001): 150-3', 1767365206);
