    ) VALUES (1385835, 'Clopidogrel', 'Chloramphenicol', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19 may reduce the efficacy of clopidogrel, whose antiplatelet effect is dependent in part on bioactivation by the isoenzyme to a pharmacologically active metabolite.', NULL, 'Based on existing data, it may be advisable to closely monitor the therapeutic efficacy of clopidogrel during concomitant treatment with CYP450 2C19 inhibitors.', NULL, 'Based on existing data, it may be advisable to closely monitor the therapeutic efficacy of clopidogrel during concomitant treatment with CYP450 2C19 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
