        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1385976, 'Chloramphenicol', 'Mephenytoin', 'Moderate', 'Chloramphenicol often markedly increases serum phenytoin levels. Toxicity has been reported. Also, phenytoin has been reported both to increase and to decrease serum chloramphenicol levels. Significant systemic absorption of otic or ophthalmic chloramphenicol products is unlikely. However, the clinician should be aware of the possibility of a drug interaction. Similar effects may occur with other hydantoins.', NULL, 'This combination usually should be avoided if possible.', NULL, 'This combination usually should be avoided if possible. Patients should be closely observed for signs of hydantoin toxicity if these drugs must be given concurrently and serum phenytoin concentrations should be monitored. Patients should be advised to notify their physician if they experience symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia. The hydantoin dose may need to be decreased.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
