        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1385977, 'Chloramphenicol', 'Methylphenobarbital', 'Moderate', 'Barbiturates may increase the hepatic metabolism of chloramphenicol. Serum chloramphenicol levels and effectiveness may be decreased. Coadministration also may enhance the pharmacologic effect of barbiturates. The proposed mechanism is induction of chloramphenicol metabolism by barbiturates and inhibition of barbiturate metabolism by chloramphenicol.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate. Dosage should be increased as needed. The patient also should be monitored for barbiturate toxicity.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
