INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104034, 'Selexipag', 'Nitroprusside', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104153/', '[1] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[2] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985): 1070-1[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104035, 'Sildenafil', 'Nitroprusside', 'Major', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of organic nitrates. Severe hypotension, syncope, or myocardial ischemia may result from use of the combination. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE5 inhibitors prevent degradation of cGMP while nitrates promote its synthesis.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated. A suitable time interval following sildenafil or vardenafil use for the safe administration of nitrates has not been determined. For patients treated with avanafil or tadalafil, at least 12 hours after the last dose of avanafil and 48 hours after the last dose of tadalafil are recommended before nitrate administration, should it be deemed medically necessary in a life-threatening situation. Even then, nitrates should only be administered under close medical supervision with appropriate hemodynamic monitoring. Patients should be advised to seek immediate medical attention if they experience anginal chest pain after taking a PDE5 inhibitor.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104154/', '[1] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[4] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[5] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104036, 'Iodide I-123', 'Nitroprusside', 'Moderate', 'INTERVAL: Certain drugs may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward.', NULL, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward. Salicylates, steroids, anticoagulants, antihistamines, antiparasitics, penicillins, sulfonamides, tolbutamide, thiopental, and nitroprusside should generally be withheld for 1 week. Amiodarone, lithium, and benzodiazepines should be withheld for at least 4 weeks.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104155/', '[1] "Product Information. Sodium Iodide I-123 (sodium iodide I-123)." GE Healthcare, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767363023);
