INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104055, 'Vardenafil', 'Nitroprusside', 'Major', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of organic nitrates. Severe hypotension, syncope, or myocardial ischemia may result from use of the combination. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE5 inhibitors prevent degradation of cGMP while nitrates promote its synthesis.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated.', NULL, 'The use of PDE5 inhibitors in patients receiving organic nitrates or other nitric oxide donors, either regularly or intermittently, is considered contraindicated. A suitable time interval following sildenafil or vardenafil use for the safe administration of nitrates has not been determined. For patients treated with avanafil or tadalafil, at least 12 hours after the last dose of avanafil and 48 hours after the last dose of tadalafil are recommended before nitrate administration, should it be deemed medically necessary in a life-threatening situation. Even then, nitrates should only be administered under close medical supervision with appropriate hemodynamic monitoring. Patients should be advised to seek immediate medical attention if they experience anginal chest pain after taking a PDE5 inhibitor.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104174/', '[1] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[4] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[5] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104056, 'Verteporfin', 'Nitroprusside', 'Moderate', 'Drugs that decrease clotting, vasoconstriction, or platelet aggregation may interfere with the therapeutic effect of verteporfin. These agents may antagonize the physiologic responses to local damage of neovascular endothelium induced by activated verteporfin, which include the release of procoagulant and vasoactive factors that result in vessel occlusion. However, the clinical significance is unknown.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy.', NULL, 'If possible, agents such as anticoagulants, thrombolytics, platelet inhibitors, nonsteroidal anti-inflammatory drugs, ginkgo, dong quai, and vasodilators should be discontinued prior to verteporfin therapy. Otherwise, the potential for diminished efficacy of verteporfin should be considered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104175/', '[1] "Product Information. Visudyne (verteporfin)" Valeant Pharmaceuticals, Costa Mesa, CA.[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.', 1767363023);
