INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104073, 'Dobutamine', 'Desmopressin', 'Moderate', 'Concomitant use of vasopressin or desmopressin with catecholamines is expected to result in an additive effect on mean arterial blood pressure and other hemodynamic parameters. In addition, although the combination noradrenaline (norepinephrine) and vasopressin is clinically used to improve hemodynamic parameters, noradrenaline may also decrease the antidiuretic effect of vasopressin.', NULL, 'The use of injectable vasopressin or large doses of intranasal or oral desmopressin with other pressor agents should only be done with careful hemodynamic monitoring.', NULL, 'The use of injectable vasopressin or large doses of intranasal or oral desmopressin with other pressor agents should only be done with careful hemodynamic monitoring. Vasopressin dosage may need to be adjusted. Consulting local protocols with respect to the dosage of vasopressin and the order of administration when used with noradrenaline is encouraged.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104192/', '[1] Russell JA, Walley KR, Singer J, et al. "Vasopressin versus norepinephrine infusion in patients with septic shock." N Engl J Med 358 (2008): 877-87[2] "Product Information. Vasostrict (vasopressin)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[3] "Product Information. DDAVP (desmopressin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Stimate (desmopressin)." Forest Pharmaceuticals, St. Louis, MO.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Dellinger RP, Levy MM, Rhodes A, et al. "Surviving Sepsis Campaign: Internatio