INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104087, 'Heparin', 'Desmopressin', 'Moderate', 'Drugs suspected of causing diabetes insipidus such as demeclocycline, lithium, foscarnet and clozapine may antagonize the antidiuretic effect and may decrease the pressor effect of vasopressin. Alcohol, noradrenaline and heparin also may decrease the antidiuretic effect of vasopressin when given concurrently. The mechanism of this interaction is unclear.', NULL, 'Hemodynamic monitoring is recommended.', NULL, 'Hemodynamic monitoring is recommended. In addition, vasopressin dosage may be adjusted as needed if co-administered with any of the abovementioned drugs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104206/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Vasostrict (vasopressin)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[4] "Product Information. Pitressin (vasopressin)." Parke-Davis, Morris Plains, NJ.[5] Harper KM, Knapp DJ, Criswell HE, Breese GR "Vasopressin and alcohol: A multifaceted relationship." Psychopharmacology (Berl) 235 (2018): 3363-79[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Collins GB, Brosnihan KB, Zuti RA, Messina M, Gupta MK "Neuroendocrine, fluid balance, and thirst responses to alcohol in alcoholics." Alcohol Clin Exp Res 16 (1992): 228-32[8] Hirschl MM, Derfler K, Bieglmayer C, et.al "Hormonal derangements in patients with severe alcohol intoxication." Alcohol Clin Exp Res 18 (1994): 761-6[9] "Product Information. Vasostrict (vasopressin)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[10] Taivainen H, Laitinen K, Tahtela R, Kilanmaa K, Valimaki MJ "Role of plasma vasopressin in changes of water balance accompanying acute alcohol intoxication." Alcohol Clin Exp Res 19 (1995): 759-62[11] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104088, 'Hydromorphone', 'Desmopressin', 'Moderate', 'Coadministration with opiates may increase the plasma concentrations and pharmacologic effects of oral desmopressin. The risk of water intoxication and/or hyponatremia may be increased.', NULL, 'Caution is recommended if desmopressin is used in combination with opiates.', NULL, 'Caution is recommended if desmopressin is used in combination with opiates. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek immediate medical attention if they develop early signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, drowsiness, lethargy, weakness, listlessness, headache, confusion, difficulty concentrating, memory impairment, anuria, and weight gain. Early treatment may help prevent progression to seizure, coma, respiratory arrest, and death.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104207/', '[1] Appel WC "Possible roles of normeperidine and hyponatremia in a postoperative death." Can Med Assoc J 137 (1987): 912-3[2] Sarret D, Le Berre JP, Zemraoui N "Tramadol-induced hyponatremia." Am J Kidney Dis 52 (2008): 1026; author reply 1027[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] "Product Information. Stimate (desmopressin)." Forest Pharmaceuticals, St. Louis, MO.[5] Callreus T, Lundahl J, Hoglund P, Bengtsson P "Changes in gastrointestinal motility influence the absorption of desmopressin." Eur J Clin Pharmacol 55 (1999): 305-9[6] Kokko H, Hall PD, Afrin LB "Fentanyl-asso