INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104130, 'Desmopressin', 'Tolvaptan', 'Moderate', 'Tolvaptan may antagonize the pharmacologic effects of desmopressin in the prevention or control of bleeding in patients with hemophilia or von Willebrand''s disease. The mechanism is interference with vascular vasopressin V2-receptors that are involved in coagulation factor release from endothelial cells, such as von Willebrand factor.', NULL, 'Concomitant use of tolvaptan with a V2-receptor agonist such as desmopressin is not recommended.', NULL, 'Concomitant use of tolvaptan with a V2-receptor agonist such as desmopressin is not recommended.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104249/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. DDAVP (desmopressin)." Rhone-Poulenc Rorer, Collegeville, PA.[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104131, 'Desmopressin', 'Torasemide', 'Major', 'Coadministration of desmopressin with loop diuretics or systemic or inhaled corticosteroids may increase the risk of severe hyponatremia. Loop diuretics and corticosteroids can potentiate the antidiuretic response to desmopressin by causing loss of sodium and water retention, respectively, thus coadministration may increase the risk of water intoxication and hyponatremia. Elderly patients may be particularly susceptible.', NULL, 'Concomitant use of certain desmopressin products with loop diuretics or systemic or inhaled corticosteroids is considered contraindicated.', NULL, 'Concomitant use of certain desmopressin products with loop diuretics or systemic or inhaled corticosteroids is considered contraindicated. Please consult individual product labeling for specific recommendations. Caution is advised when coadministration is required. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly and as often as clinically appropriate based on individual risk factors. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104250/', '[1] "Product Information. Stimate (desmopressin)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Nocdurna (desmopressin)." Ferring Inc, North York, ON.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Noctiva (desmopressin)." Avadel Pharmaceuticals, Chesterfield, MO.[6] "Product Information. DDAVP (desmopressin)." Rhone-Poulenc Rorer, Collegeville, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. DDAVP (desmopressin)." Rhone-Poulenc Rorer, Collegeville, PA.[9] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104132, 'Desmopressin', 'Trimipramine', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104251/', '[1] Vanhees SL, Paridaens R, Vansteenkiste JF "Syndrome of inappropriate antidiuretic hormone associated with chemotherapy-induced tumour lysis in small-cell lung cancer: case report and literature review." Ann Oncol 11 (2000): 1061-5[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Kosmidis HV, Bouhoutsou DO, Varvoutsi MC, et al. "Vincristine overdose: experience with 3 patients." Pediatr Hematol Oncol 8 (1991): 171-8[4] Maramattom BV "Duloxetine-induced syndrome of inappropriate antidiuretic hormone secretion and seizures." Neurology 66 (2006): 773-4[5] ten Holt WL, van Iperen CE, Schrijver G, Bartelink AK "Severe hyponatremia during therapy with fluoxetine." Arch Intern Med 156 (1996): 681-2[6] Miller MG "Tricyclics as a possible cause of hyponatremia in psychiatric patients." Am J Psychiatry 146 (1989): 807[7] Petersson I, Nilsson G, Hansson B-G, Hedner T "Water intoxication associated with non-steroidal anti-inflammatory drug therapy." Acta Med Scand 221 (1987): 221-3[8] Nielsen OA, Johannessen AC, Bardrum B "Oxcarbazepine-induced hyponatremia, a cross-sectional study." Epilepsy Res 2 (1988): 269-71[9] Schattner A, Skurnik Y "Fluoxetine-induced SIADH." J Am Geriatr Soc 44 (1996): 1413[10] Tanay A, Firemann Z, Yust I, Abramov AL "Chlorpropamide-induced syndrome of inappropriate antidiuretic hormone secretion." J Am Geriatr Soc 29 (1981): 334-6[11] Catalano G, Kanfer SN, Catalano MC, Alberts VA "The role of sertraline in a patient with recurrent hyponatremia." Gen Hosp Psychiatry 18 (1996): 278-83[12] Kokko H, Hall PD, Afrin LB "Fentanyl-associated syndrome of inappropriate antidiuretic horm