INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104163, 'Betamethasone', 'Mestranol', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', NULL, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104282/', '[1] Meffin PJ, Wing LM, Sallustio BC, Brooks PM "Alterations in prednisolone as a result of oral contraceptive use and dose." Br J Clin Pharmacol 17 (1984): 655-64[2] Legler UF, Benet LZ "Marked alterations in dose-dependent prednisolone kinetics in women taking oral contraceptives." Clin Pharmacol Ther 39 (1986): 425-9[3] Frey BM, Schaad HJ, Frey FJ "Pharmacokinetic interaction of contraceptive steroids with prednisone and prednisolone." Eur J Clin Pharmacol 26 (1984): 505-11[4] "Product Information. Premarin (conjugat