INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104239, 'Exemestane', 'Mestranol', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104358/', '[1] "Product Information. Arimidex (anastrozole)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Aromasin (exemestane)" Pharmacia & Upjohn, Kalamazoo, MI.[3] "Product Information. Femara (letrozole)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363023);
