    ) VALUES (1386381, 'Chlordiazepoxide', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of sedative-hypnotic agents. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be anticipated.', NULL, 'The use of sodium oxybate in combination with sedative-hypnotic agents is considered contraindicated.', NULL, 'The use of sodium oxybate in combination with sedative-hypnotic agents is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
