INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1386396, 'Chlordiazepoxide', 'Tetracaine', 'Moderate', 'The concomitant use of local anesthetics and benzodiazepines may have additive CNS-depressant effects. Both types of drugs are CNS depressants.', 'DDInter', 1767317475, 'Caution is advised during concurrent use. Patients should be monitored for potentially excessive or prolonged CNS depression and other CNS adverse effects. Dosage adjustments may be required.', 'Synergism', 'Caution is advised during concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1386397, 'Chlordiazepoxide', 'Thalidomide', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 1767317475, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
