INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1386480, 'Clidinium', 'Prucalopride', 'Moderate', 'Concomitant administration of anticholinergic agents would be expected to reduce the therapeutic effects of prucalopride. Prucalopride increases bowel motility by stimulating colonic peristalsis (high-amplitude propagating contractions), an action that has been shown to be facilitated by acetylcholine release in isolated gastrointestinal tissues from various animal species.', 'DDInter', 1767317475, 'Monitoring for altered efficacy of prucalopride may be advisable during concomitant use with anticholinergic agents.', 'Absorption', 'Monitoring for altered efficacy of prucalopride may be advisable during concomitant use with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1386481, 'Clidinium', 'Pyridostigmine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', 'DDInter', 1767317475, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
