INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104686, 'Fluoxetine', 'Pimozide', 'Major', 'Coadministration with fluoxetine may significantly increase the plasma concentrations of pimozide. The proposed mechanism is fluoxetine inhibition of the metabolism of pimozide via CYP450 2D6 and 3A4. Although the interaction has not been formally studied with fluoxetine, data exist for paroxetine, a drug with similar CYP450 inhibition characteristics.', NULL, 'Given the narrow therapeutic index of pimozide and its known ability to prolong the QT interval, concomitant use of pimozide and fluoxetine is considered contraindicated.', NULL, 'Given the narrow therapeutic index of pimozide and its known ability to prolong the QT interval, concomitant use of pimozide and fluoxetine is considered contraindicated. Due to the long half-life of fluoxetine and its metabolite norfluoxetine, the risk of an interaction may exist for an extended period (up to several weeks) after discontinuation of fluoxetine.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104805/', '[1] Dsouza DC, Bennett A, Abidargham A, Krystal JH "Precipitation of a psychoneuromotor syndrome by fluoxetine in a haloperidol-treated schizophrenic patient." J Clin Psychopharmacol 14 (1994): 361-3[2] Nicholson SD "Extra pyramidal side effects associated with paroxetine." West Engl Med J 107 (1992): 90-1[3] Palop V, Jimenez MJ, Catalan C, MartinezMir I "Acute dystonia associated with fluvoxamine-metoclopramide." Ann Pharmacother 33 (1999): 382[4] Lock JD, Gwirtsman HE, Targ EF "Possible adverse drug interactions between fluoxetine and other psychotropics." J Clin Psychopharmacol 10 (1990): 383-