INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (104902, 'Fosamprenavir', 'Trazodone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of trazodone, which is primarily metabolized by the isoenzyme.', NULL, 'If concomitant use cannot be avoided, a lower dosage of trazodone should be considered during coadministration with a potent CYP450 3A4 inhibitor.', NULL, 'If concomitant use cannot be avoided, a lower dosage of trazodone should be considered during coadministration with a potent CYP450 3A4 inhibitor. Pharmacologic response to trazodone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the trazodone dosage adjusted as necessary. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105021/', '[1] "Product Information. Oleptro (traZODone)." Labopharm Inc, Laval, AL.[2] Mazur A, Strasberg B, Kusniec J, Sclarovsky S "QT prolongation and polymorphous ventricular tachycardia associated with trasodone-amiodarone combination." Int J Cardiol 52 (1995): 27-9[3] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[4] Dattilo PB, Nordin C "Prolonged QT associated with an overdose of trazodone." J Clin Psychiatry 68 (2007): 1309-10[5] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point,