    ) VALUES (1386946, 'Diazoxide', 'Chlorothiazide', 'Moderate', 'The combination of thiazide diuretics and diazoxide may result in profound and prolonged hyperglycemia. The mechanism may be related to additive hyperglycemic effects.', NULL, 'Blood glucose monitoring is recommended during coadministration.', NULL, 'Blood glucose monitoring is recommended during coadministration.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
