        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1386947, 'Dicoumarol', 'Chlorothiazide', 'Minor', 'Thiazide diuretics may reduce intravascular volume. Higher plasma levels of clotting factors and decreased anticoagulant effect may result; however the effects may not be clinically significant. Management consists of monitoring coagulation parameters during the initial phase of coadministration. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
