        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1386954, 'Dolasetron', 'Chlorothiazide', 'Major', 'The risk of arrhythmia may be increased in patients taking dolasetron and diuretics. Dolasetron induced ECG changes (PR and QTc prolongation; QRS widening) have been observed in healthy volunteers and in controlled clinical trials. Diuretics may further predispose the patient to arrhythmia by inducing electrolyte abnormalities.', NULL, 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', NULL, 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
