INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105131, 'Trazodone', 'Rolapitant', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Rolapitant is a moderate CYP450 2D6 inhibitor, with inhibitory effect lasting at least 7 days after a single dose.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105250/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc., Waltham, MA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105132, 'Trazodone', 'Romidepsin', 'Moderate', 'Trazodone may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of trazodone with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of trazodone with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Trazodone should also not be used in patients with risk factors for QT prolongation. Hypokalemia and hypomagnesemia should be corrected prior to initiation of trazodone treatment and periodically monitored. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When trazodone is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105251/', '[1] "Product Information. Oleptro (traZODone)." Labopharm Inc, Laval, AL.[2] Mazur A, Strasberg B, Kusniec J, Sclarovsky S "QT prolongation and polymorphous ventricular tachycardia associated with trasodone-amiodarone combination." Int J Cardiol 52 (1995): 27-