INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105283, 'Quinapril', 'Omadacycline', 'Moderate', 'INTERVAL: Administration of a tetracycline with quinapril may significantly decrease tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the magnesium excipient in quinapril.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of tetracyclines and magnesium-containing products should be separated by two to four hours.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of tetracyclines and magnesium-containing products should be separated by two to four hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105402/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[3] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[4] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[5] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[6] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105284, 'Ranitidine (bismuth citrate)', 'Omadacycline', 'Moderate', 'INTERVAL: Administration of a bismuth-containing preparation within two to three hours of a tetracycline may significantly decrease serum tetracycline concentrations. Data are available for tetracycline and doxycycline. The proposed mechanism is chelation of tetracycline by bismuth.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours.', NULL, 'Administration of a tetracycline and bismuth-containing preparation should be separated by two to three hours. Patients should be monitored for diminished tetracycline efficacy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105403/', '[1] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[2] Albert KS, Welch RD, DeSante KA, DiSanto AR "Decreased tetracycline bioavailability caused by a bismuth subsalicylate antidiarrheal mixture." J Pharm Sci 68 (1979): 586-8[3] Ericsson CD, Feldman S, Pickering LK, Cleary TG "Influence of subsalicylate bismuth on absorption of doxycycline." JAMA 247 (1982): 2266-7[4] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105285, 'Sucralfate', 'Omadacycline', 'Moderate', 'INTERVAL: Simultaneous administration of sucralfate may interfere with the absorption of tetracyclines and reduce their oral bioavailability. Sucralfate is an aluminum salt of a sulfated disaccharide. The aluminum moiety can dissociate at gastric pH and chelate with tetracyclines to form an insoluble complex that is poorly absorbed from the gastrointestinal tract. Reduced antibiotic efficacy may occur.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105404/', '[1] "Product Information. Carafate (sucralfate)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] McCarthy DM "Sucralfate." N Engl J Med 325 (1991): 1017-25[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105286, 'Iron sucrose', 'Omadacycline', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of tetracyclines. The proposed mechanism is chelation of tetracyclines to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide. Omadacycline administration with sucroferric oxyhydroxide should be separated by 4 hours. No dosing recommendations are available for use with other tetracyclines, although patients should consider following the same precaution.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105405/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Velphoro (sucroferric oxyhydroxide)." Fresenius Medical Care North America, Lexington, MA.[4] Campbell NR, Hasinoff BB "Iron supplements: a common cause of drug interactions." Br J Clin Pharmacol 31 (1991): 251-5[5] Greenberger NJ "Absorption of tetracyclines: interference by iron." Ann Intern Med 74 (1971): 792-3[6] Neuvonen PJ, Gothoni G, Hackman R, Bjorksten K "Interference of iron with the absorption of tetracyclines in man." Br Med J 4 (1970): 532-4[7] Gothoni G, Neuvonen PJ, Mattila M, Hackman R "Iron-tetracycline interaction: effect of time interval betwe