INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105288, 'Typhoid vaccine (live)', 'Omadacycline', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105407/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] CDC. Centers for Disease Control and Prevention "Typhoid & Paratyphoid Fever. Available from: URL: http://wwwnc.cdc.gov/travel/yellowbook/2014/chapter-3-infectious-diseases-related-to-travel/typhoid-and-paratyphoid-fever." ([2013]):', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105289, 'Verteporfin', 'Omadacycline', 'Moderate', 'Concomitant use of verteporfin with other known photosensitizing agents may increase the risk of photosensitivity reactions. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of skin or eyes to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) for 5 days after administration of verteporfin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105408/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Visudyne (verteporfin)" Valeant Pharmaceuticals, Costa Mesa, CA.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105290, 'Vitamin A', 'Omadacycline', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. Both acute and chronic vitamin A intoxication have been identified as secondary causes of pseudotumor cerebri.', NULL, 'Caution is advised if vitamin A is coadministered with tetracyclines.', NULL, 'Caution is advised if vitamin A is coadministered with tetracyclines. Concomitant use of most retinoids with tetracyclines is considered contraindicated. Patients treated with any of these agents should be advised to discontinue treatment and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105409/', '[1] Lewis PA, Kearney PJ "Pseudotumor cerebri induced by minocycline treatment for acne vulgaris." Acta Derm Venereol 77 (1997): 83[2] Cuddihy J "Case report of benign intercranial hypertension secon