INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105323, 'Fluconazole', 'Ritonavir', 'Minor', 'The coadministration with fluconazole may slightly increase the plasma concentrations of ritonavir. The proposed mechanism is fluconazole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ritonavir. Other azole antifungal agents are expected to affect ritonavir in a similar fashion but possibly to a greater extent, since fluconazole is a relatively weak inhibitor of CYP450 3A4. No special precautions are necessary when fluconazole is given with ritonavir.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105443/', '[1] Koks CH, Crommentuyn KM, Hoetelmans RM, et al. "The effect of fluconazole on ritonavir and saquinavir pharmacokinetics in HIV-1-infected individuals." Br J Clin Pharmacol 51 (2001): 631-5[2] Cato A 3rd, Cao G, Hsu A, Cavanaugh J, Leonard J, Granneman R "Evaluation of the effect of fluconazole on the pharmacokinetics of ritonavir." Drug Metab Dispos 25 (1997): 1104-6[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767363023);
