INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105325, 'Gemfibrozil', 'Paritaprevir', 'Moderate', 'Coadministration with gemfibrozil may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 transporter. The mechanism is decreased clearance due to inhibition of OATP 1B1-mediated hepatic uptake by gemfibrozil and its metabolite, gemfibrozil 1-O-beta-glucuronide. The clinical relevance of this interaction is unknown.', NULL, 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil.', NULL, 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil. Clinical and laboratory monitoring may be appropriate for some drugs whenever gemfibrozil is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105446/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lasix (furosemide)." sanofi-aventis , Bridgewater, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (105326, 'Grepafloxacin', 'Ritonavir', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', NULL, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105447/', '[1] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363023);
